import React, { useState } from 'react'
import './Todo.css';
import axios from 'axios';
const Todo = () => {
    const [newtask, setnewtask] = useState("")
    const [datas, setDatas] = useState([
        { content: "hai" },
        { content: "hai" },
        { content: "hai" },
        { content: "hai" }
])
console.log(newtask);
    const add = async() => {
        try {
            if(newtask.length > 0){
                const response = await axios.post("http://localhost:8080/api/task/addtask",{
                    taskname : newtask,
                })
                console.log(response.data);
                setDatas(prev => [...prev, newtask])
                setnewtask("")
            }
        } catch (error) {
            lo
        }
    }



  return (
    <div className='todo'>
        <h1>Simple Todo</h1>
        <div className='todo_container'>
            <div className='todo_container-top'>
                <input className='todo_container-top-input' type="text" placeholder='Enter a task' value={newtask} onChange={(e)=>setnewtask(e.target.value)}/>
                <button className='todo_container-top-add' onClick={add}>Add</button>
            </div>
            <div class="todo_page-middle">
                {
                    datas.map((item,index)=>(
                        <div className='todo_page-middle-tasks'>
                            <p class="task-content">{item.content}</p>
                            <button class="task-mark">Mark</button>
                            <button class="task-delete">Delete</button>
                        </div>
                    ))
                }
            </div>
        </div>
    </div>
  )
}

export default Todo
